import React, { useState, useRef, useEffect } from 'react';
import './Chatbot.css';
import Logo from '../../assets/favicon.png';

const Chatbot = () => {
  const [isExpanded, setIsExpanded] = useState(false);
  const iframeRef = useRef(null);

  const handleButtonClick = () => {
    setIsExpanded(prevState => !prevState);
  };

  useEffect(() => {
    const injectCSS = () => {
      const iframe = iframeRef.current;
      if (iframe && iframe.contentWindow) {
        const style = iframe.contentWindow.document.createElement('style');
        style.textContent = `
          .hyphens-auto.text-wrap.break-words.rounded-[20px].text-left.text-sm.leading-5.antialiased.ml-auto.rounded-br.px-4.py-2.whitespace-pre.border-zinc-200.font-sans.max-w-[min(calc(100%-40px),65ch)].group-data-[theme=dark]:border-zinc-800[data-user="true"] {
            background-color: #4a90e2 !important;
            font-family: 'Arial', sans-serif !important;
          }
          .custom-chatbot-class {
            /* Add your custom styles here */
            border: 2px solid #4a90e2;
            border-radius: 10px;
          }
        `;
        iframe.contentWindow.document.head.appendChild(style);

        // Add the custom class to the body or a specific element
        const chatbotContainer = iframe.contentWindow.document.body;
        chatbotContainer.classList.add('custom-chatbot-class');
      }
    };

    if (isExpanded) {
      // Wait for the iframe to load before injecting CSS
      const iframe = iframeRef.current;
      if (iframe) {
        iframe.addEventListener('load', injectCSS);
      }
    }

    return () => {
      const iframe = iframeRef.current;
      if (iframe) {
        iframe.removeEventListener('load', injectCSS);
      }
    };
  }, [isExpanded]);

  return (
    <>
      <div className={`Container ${isExpanded ? 'expanded' : ''}`}>
        <div className="Chatbot">
          <iframe
            ref={iframeRef}
            src="https://www.chatbase.co/chatbot-iframe/a09RecgAino-8F_ct_hwg"
            width="100%"
            style={{ height: '100%' }}
            frameBorder="0"
            title="Chatbot"
          ></iframe>
          <div className='hide_watermark'><p>transforming ideas digital realities</p></div>
        </div>
      </div>
      <button className="floating-button" onClick={handleButtonClick}>
        <img src={Logo} alt="Chatbot Logo" />
      </button>
    </>
  );
};

export default Chatbot;
