// File path: src/components/Chatbot/Chatbot.jsx

import React, { useState, useRef, useEffect } from 'react';
import './Chatbot.css';
import Logo from '../../assets/favicon.png';

const Chatbot = () => {
  const [showIframe, setShowIframe] = useState(false);
  const iframeRef = useRef(null);

  const handleButtonClick = () => {
    setShowIframe(prevState => !prevState);
  };

  useEffect(() => {
    if (showIframe && iframeRef.current) {
      const iframe = iframeRef.current;

      const injectCSS = () => {
        if (iframe.contentDocument) {
          const styleElement = iframe.contentDocument.createElement('style');
          styleElement.innerHTML = `
            footer {
              display: none !important;
            }
          `;
          iframe.contentDocument.head.appendChild(styleElement);
        }
      };

      iframe.addEventListener('load', injectCSS);

      return () => {
        iframe.removeEventListener('load', injectCSS);
      };
    }
  }, [showIframe]);

  return (
    <>
      <div className="Container">
        <div className="Chatbot">
          {showIframe && (
            <>
              <iframe
                ref={iframeRef}
                src="https://www.chatbase.co/chatbot-iframe/a09RecgAino-8F_ct_hwg"
                width="100%"
                style={{ height: '100%', minHeight: '80vh' }}
                frameBorder="0"
                title="Chatbot"
              ></iframe>
              <div className="bottom-line"></div>
            </>
          )}
        </div>
      </div>
      <button className="floating-button" onClick={handleButtonClick}>
        <img src={Logo} alt="Chatbot Logo" />
      </button>
    </>
  );
};

export default Chatbot;
