import React, { useState, useRef, useEffect } from 'react';
import './Chatbot.css';
import Logo from '../../assets/favicon.png';

const Chatbot = () => {
  const [isExpanded, setIsExpanded] = useState(false);
  const iframeRef = useRef(null);

  const handleButtonClick = () => {
    setIsExpanded(prevState => !prevState);
  };

  useEffect(() => {
    const injectCSSAndJS = () => {
      const iframe = iframeRef.current;
      if (iframe && iframe.contentWindow) {
        const doc = iframe.contentWindow.document;

        // Inject CSS
        const style = doc.createElement('style');
        style.textContent = `
          .hyphens-auto.text-wrap.break-words.rounded-\\[20px\\].text-left.text-sm.leading-5.antialiased.ml-auto.rounded-br.px-4.py-2.whitespace-pre.border-zinc-200.font-sans.max-w-\\[min\\(calc\\(100\\%-40px\\)\\,65ch\\)\\].group-data-\\[theme\\=dark\\]\\:border-zinc-800[data-user="true"] {
            background-color: #4a90e2 !important; /* Change this to your desired background color */
            font-family: 'Arial', sans-serif !important; /* Change this to your desired font family */
          }
        `;
        doc.head.appendChild(style);

        // Inject JavaScript
        const script = doc.createElement('script');
        script.textContent = `
          document.addEventListener('DOMContentLoaded', function() {
            const targetElement = document.querySelector('.target-class'); // Change this selector to the element you want to target
            if (targetElement) {
              targetElement.classList.add('new-class'); // Add the new class
              targetElement.innerHTML = 'Your new content'; // Overwrite the content
            }
          });
        `;
        doc.head.appendChild(script);
      }
    };

    if (isExpanded) {
      const iframe = iframeRef.current;
      if (iframe) {
        iframe.addEventListener('load', injectCSSAndJS);
      }
    }

    return () => {
      const iframe = iframeRef.current;
      if (iframe) {
        iframe.removeEventListener('load', injectCSSAndJS);
      }
    };
  }, [isExpanded]);

  return (
    <>
      <div className={`Container ${isExpanded ? 'expanded' : ''}`}>
        <div className="Chatbot">
          <iframe
            ref={iframeRef}
            src="https://www.chatbase.co/chatbot-iframe/a09RecgAino-8F_ct_hwg"
            width="100%"
            style={{ height: '100%' }}
            frameBorder="0"
            title="Chatbot"
          ></iframe>
          <div className='hide_watermark'> <p>transforming ideas digital realities</p></div>
        </div>
      </div>
      <button className="floating-button" onClick={handleButtonClick}>
        <img src={Logo} alt="Chatbot Logo" />
      </button>
    </>
  );
};

export default Chatbot;
