// File path: src/components/Chatbot/Chatbot.jsx

import React, { useState, useRef, useEffect } from 'react';
import './Chatbot.css';
import Logo from '../../assets/favicon.png';

const Chatbot = () => {
  const [isExpanded, setIsExpanded] = useState(false);
  const iframeRef = useRef(null);

  const handleButtonClick = () => {
    setIsExpanded(prevState => !prevState);
  };

  useEffect(() => {
    const iframe = iframeRef.current;
    if (iframe) {
      iframe.onload = () => {
        iframe.contentWindow.postMessage(
          {
            type: 'APPLY_STYLES',
            styles: {
              backgroundColor: 'rgb(24, 24, 27)',
              color: 'rgb(255, 255, 255)',
              fontFamily: 'Arial, sans-serif'
            }
          },
          '*'
        );
      };
    }
  }, [isExpanded]);

  return (
    <>
      <div className={`Container ${isExpanded ? 'expanded' : ''}`}>
        <div className="Chatbot">
          <iframe
            ref={iframeRef}
            src="https://www.chatbase.co/chatbot-iframe/a09RecgAino-8F_ct_hwg"
            width="100%"
            style={{ height: '100%' }}
            frameBorder="0"
            title="Chatbot"
          ></iframe>
          <div className='hide_watermark'> <p>transforming ideas digital realities</p></div>
        </div>
      </div>
      <button className="floating-button" onClick={handleButtonClick}>
        <img src={Logo} alt="Chatbot Logo" />
      </button>
    </>
  );
};

export default Chatbot;
