import React, { useState, useRef, useEffect } from 'react';
import './Chatbot.css';

const Chatbot = () => {
  const [showIframe, setShowIframe] = useState(false);
  const iframeRef = useRef(null);

  const handleButtonClick = () => {
    setShowIframe(prevState => !prevState);
  };

  useEffect(() => {
    if (showIframe && iframeRef.current) {
      const iframe = iframeRef.current;

      const injectCSS = () => {
        if (iframe.contentDocument) {
          const footerStyle = iframe.contentDocument.createElement('class');
          footerStyle.innerHTML = `
            footer {
              display: none !important;
            }
          `;
          iframe.contentDocument.head.appendChild(footerStyle);
        }
      };

      iframe.addEventListener('load', injectCSS);

      return () => {
        iframe.removeEventListener('load', injectCSS);
      };
    }
  }, [showIframe]);

  return (
    <div className='Container'>
      <div className="Chatbot">
        {showIframe && (
            <>
          <iframe
            ref={iframeRef}
            src="https://www.chatbase.co/chatbot-iframe/a09RecgAino-8F_ct_hwg"
            width="100%"
            style={{ height: '100%', minHeight: '50vh' }}
            frameBorder="0"
            title="Chatbot"
          ></iframe>
          </>

          
        )}
      </div>
      <button className="floating-button" onClick={handleButtonClick}>
        {showIframe ? 'Hide Chatbot' : 'Open Chatbot'}
      </button>
    </div>
  );
};

export default Chatbot;
