// File path: src/components/Chatbot/Chatbot.jsx
import React, { useState } from 'react';
import './Chatbot.css';
import Logo from '../../assets/favicon.png';

const Chatbot = () => {
  const [isExpanded, setIsExpanded] = useState(false);

  const handleButtonClick = () => {
    setIsExpanded(prevState => !prevState);
  };

  return (
    <>
      <div className={`Container ${isExpanded ? 'expanded' : ''}`}>
        <div className="Chatbot">
          <div className="custom-header">My Custom Chatbot</div>
          <div className="iframe-wrapper">
            <iframe
              src="https://www.chatbase.co/chatbot-iframe/a09RecgAino-8F_ct_hwg"
              width="100%"
              style={{ height: 'calc(100% - 60px)' }}
              frameBorder="0"
              title="Chatbot"
            ></iframe>
          </div>
          <div className='custom-footer'>
            <p>Transforming ideas into digital realities</p>
          </div>
        </div>
      </div>
      <button className="floating-button" onClick={handleButtonClick}>
        <img src={Logo} alt="Chatbot Logo" />
      </button>
    </>
  );
};

export default Chatbot;
