import React, { useState, useEffect } from 'react';
import './Chatbot.css';
import Logo from '../../assets/favicon.png';

const Chatbot = () => {
  const [isExpanded, setIsExpanded] = useState(false);

  const handleButtonClick = () => {
    setIsExpanded(prevState => !prevState);
  };

  useEffect(() => {
    const injectScripts = () => {
      const script1 = document.createElement('script');
      script1.textContent = `
        window.embeddedChatbotConfig = {
          chatbotId: "a09RecgAino-8F_ct_hwg",
          domain: "www.chatbase.co"
        }
      `;
      document.head.appendChild(script1);

      const script2 = document.createElement('script');
      script2.src = "https://www.chatbase.co/embed.min.js";
      script2.setAttribute('chatbotId', 'a09RecgAino-8F_ct_hwg');
      script2.setAttribute('domain', 'www.chatbase.co');
      script2.defer = true;
      document.head.appendChild(script2);
    };

    if (isExpanded) {
      injectScripts();
    }

    return () => {
      // Clean up scripts when the component unmounts or the state changes
      const script1 = document.querySelector('script[chatbotId]');
      const script2 = document.querySelector('script[src="https://www.chatbase.co/embed.min.js"]');
      if (script1) script1.remove();
      if (script2) script2.remove();
    };
  }, [isExpanded]);

  return (
    <>
      <div className={`Container ${isExpanded ? 'expanded' : ''}`}>
        <div className="Chatbot">
          {/* Placeholder div for the chatbot */}
          <div id="chatbot-container" style={{ height: '100%' }}></div>
          <div className='hide_watermark'>
            <p>transforming ideas into digital realities</p>
          </div>
        </div>
      </div>
      <button className="floating-button" onClick={handleButtonClick}>
        <img src={Logo} alt="Chatbot Logo" />
      </button>
    </>
  );
};

export default Chatbot;
